import React from "react";
import axios from "axios";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Grid,
  SearchBar,
  Chip,
} from "@material-ui/core";
import "./App.css";
import Header from "./components/Header";

const baseURL = "http://localhost:8000/getpopular";

function App() {
  const [info, setInfo] = React.useState(null);

  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setInfo(response.data);
    });
  }, []);

  const requestSearch = (searchedVal) => {
    const filteredRows = info.filter((row) => {
      return row.Item_name.toLowerCase().includes(searchedVal.toLowerCase());
    });

    setInfo(filteredRows);
  };

  const cancelSearch = (searchedVal) => {
    axios.get(baseURL).then((response) => {
      setInfo(response.data);
    });
  };
  console.log(info);

  if (!info) return "We are working at the moment";

  //for the search bar functionality

  //const [rows, setRows] = useState<food[]>(originalRows);

  return (
    <div className="App">
      <>
        <Header />
        <br />
        <br />
        <br />
        <Grid
          container
          direction="column"
          alignItems="center"
          //style={{ minHeight: "100vh" }}
        >
          <Grid item xs={12}>
            <div
              style={{
                top: "100vh",
                width: "80vw",
                textAlign: "center",
                marginTop: "30px",
              }}
            >
              <form>
                <input
                  type="text"
                  placeholder="Search the popular products using their name!"
                  name="search"
                  autoFocus
                  style={{
                    height: "6vh",
                    width: "80vw",
                    borderRadius: "30x",
                    textAlign: "center",
                    boxShadow: "2px 2px 2px 2px #0078AE",
                  }}
                  onChange={(event) => {
                    event.target.value == ""
                      ? cancelSearch(event.target.value)
                      : requestSearch(event.target.value);
                  }}
                ></input>
              </form>
            </div>
          </Grid>
          <Grid item xs={12}>
            <Paper style={{ width: "80vw" }}>
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Order ID</TableCell>
                      <TableCell align="center">Name</TableCell>
                      <TableCell align="center">Tags</TableCell>
                    </TableRow>
                  </TableHead>

                  <TableBody>
                    {info.map((res) => (
                      <TableRow>
                        <TableCell component="th" scope="rh">
                          {res.Order_ID}
                        </TableCell>
                        <TableCell align="center">{res.Item_name}</TableCell>
                        <TableCell align="center">
                          <Chip label={`${res.quantity} purchased recently`} />
                          <br />
                          <Chip label={res.recent_time} />
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Paper>
          </Grid>
        </Grid>
      </>
    </div>
  );
}

export default App;
